function sProcessId=wAddParam(Wasdi, sKey, sValue)% Adds a parameter to current processor% Syntax% wAddParam(Wasdi, sKey, sValue)% % INPUT%  Wasdi: Wasdi object created after the wasdilib call%	 sKey: a string to be used as key for the param%	 sValue: a string to be used as a value%  if exist("Wasdi") < 1     disp('Wasdi variable does not exist')    return   end      Wasdi.addParam(sKey, sValue);end