function sWorkspaceId=wCreateWorkspace(Wasdi, sWorkspaceName, sNodeCode='')% Copy file to SFTP folder, asynchronous version% Syntax% wCreateWorkspace(Wasdi, sWorkspaceName, sNodeCode)% % INPUT%  Wasdi: Wasdi object created after the wasdilib call%  sWorkspaceName: the name of the workspace%  sNodeCode: the code of the node, optional%% OUTPUT% sWorkspaceId: the ID of the workspace (empty in case of error)  if exist("Wasdi") < 1     disp('Wasdi variable does not exist')    return   end      Wasdi.createWorkspace(sWorkspaceName, sNodeCode)end