function aoProcesses=wGetProcessesByWorkspace(Wasdi, iStartIndex=0, iEndIndex=[], sStatus=[], sOperationType=[], sNamePattern=[])% Get a paginated list of processes in the active workspace % Syntax% asProcesses=wgetProcessesByWorkspace(Wasdi, iStartIndex, iEndIndex=[], sStatus=[], sOperationType=[], sNamePattern=[]?)% % INPUT%   Wasdi: Wasdi object created after the wasdilib call%   iStartIndex: first index%   iEndIndex: last index%   sStatus: filter by statuses%   sOperationType: filter by operation name%   sNamePattern: filter by name%%:Returns:%   asProcesses: list of processes%  if exist("Wasdi") < 1     disp('Wasdi variable does not existst')    return   end      asJsonProcesses = Wasdi.getProcessesByWorkspaceAsListOfJson(iStartIndex, iEndIndex, sStatus, sOperationType, sNamePattern)      aoProcesses = []      for iProcess= 0:asJsonProcesses.size()-1    aoProcesses = [aoProcesses,loadjson(asJsonProcesses.get(iProcess))];   end   end