function wSetDownloadActive(Wasdi, iActive)%Set the download active flag%Syntax%wSetDownloadActive(Wasdi, iActive)%%:param Wasdi: Wasdi object created after the wasdilib call%:param iActive: true/false%  if exist("Wasdi") < 1     disp('Wasdi variable does not existst')    return   end     Wasdi.setDownloadActive(iActive);end