function wSetPassword(Wasdi, sPassword)%Set the WASDI user password%Syntax%wSetPassword(Wasdi, sPassword)%%:param Wasdi: Wasdi object created after the wasdilib call%:param sPassword: the password  if exist("Wasdi") < 1     disp('Wasdi variable does not existst')    return   end      Wasdi.setPassword(sPassword)end