function wSetPayload(Wasdi, sPayload)% Writes a Payload in a process% Syntax% sStatus =wSetProcessPayload(Wasdi, sProcessId, sData);% % INPUT%    Wasdi: Wasdi object created after the wasdilib call%    sPayload: the process payload  if exist("Wasdi") < 1     disp('Wasdi variable does not existst')    return   end      sStatus = char(Wasdi.setPayload(sPayload));  end